	-moz-box-sizing: border-box;
	box-sizing: border-box;
}


#output-block .header {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 2em;
}

#output-block .radio-group {
	display: inline-block;
	float: right;
	text-align: right;
	padding: 0.5em 0 0 0;
}

#output-block .header h2 {
	float: left;
	margin: 0;
}

#output-block .header select {
	position: relative;
	top: 0.1em;
	float: left;
	clear: left;
	font-size: inherit;
	font-family: inherit;
	z-index: 7;
}

#output-block .header label {
	position: relative;
	z-index: 7;
}

#output-block .header p {
	float: left;
	clear: left;
	margin: 0;
}

#output-block .bar-chart {
	position: relative;
	padding: 0 0 3em 0;
	width: 100%;
	height: 100%;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

#output-block .bar-group {
	position: relative;
	float: left;
	height: 100%;
	text-align: center;
}

#output-block .month-label {
	position: absolute;
	bottom: -2em;
	left: 0;
	width: 100%;
}

#output-block .bar-outer {
	position: absolute;
	width: 100%;
	padding: 0 1px;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

#output-block .bar-outer.positive {
