styles:
#output-block p {
	font-size: 0.8em;
	margin: 0 0 0.2em 0;
}

#output-block div {
	height: 1.6em;
	margin: 0 0 0.5em 0;
	background-color: #f4f4f4;
}

.color-mixer {
	height: 100%;
}

.color-mixer th, .color-mixer td {
	height: 20%;
}

.color-mixer th:first-child, .color-mixer td:first-child {
	text-align: right;
}

.color-mixer th:last-child, .color-mixer td:last-child {
	width: 70%;
}

.color-mixer div {
	width: 100%;
	max-width: 100%;
	height: 100%;
}

