javascript:
getJSON( '/temperature.json' ).then( function ( cities ) {
  ractive.set( 'cities', cities );

  ractive.observe( 'selectedIndex', function ( index ) {
    this.animate( 'selectedCity', cities[ index ], {
      easing: 'easeOut',
      duration: 300
    });
  });
});

var ractive = new Ractive({
  el: output,
  template: template,
  data: {
    format: function ( val ) {
      if ( this.get( 'degreeType' ) === 'fahrenheit' ) {
        // convert celsius to fahrenheit
        val = ( val * 1.8 ) + 32;
      }

      return val.toFixed( 1 ) + '°';
    },
    monthNames: [ 'J', 'F', 'M', 'A', 'M', 'J', 'J', 'A', 'S', 'O', 'N', 'D' ]
  },

  computed: {
    // The SVG path is a computed value that updates whenever
    // its dependencies (declared with `this.get()`) change
    svgPath: function () {
      var data = this.get( 'selectedCity.months' ),
          xScale = this.get( 'xScale' ),
          yScale = this.get( 'yScale' );

          // Get an array of points representing the edges of
          // the temperature band...
          high = getPoints( 'high' ),
          low = getPoints( 'low' );

      // ...and join them together
      return high.concat( low.reverse() ).join( ' ' );

      function getPoints ( highOrLow ) {
        var result = data.map( function ( month, i ) {
          return xScale( i + 0.5 ) + ',' + yScale( month[ highOrLow ] );
        });

        // Add the December value in front of January, and the January value after
        // December, to show the cyclicality
        result.unshift( xScale( -0.5 ) + ',' + yScale( data[ data.length - 1 ][ highOrLow ] ) );
        result.push( xScale( data.length + 0.5 ) + ',' + yScale( data[0][ highOrLow ] ) );

        return result;
      }
    },

    // When the dimensions of the container change, we need
    // to recreate the linear scaling functions
    xScale: function () {
      return linearScale([ 0, 12 ], [ 0, this.get( 'width' ) ]);
    },
    yScale: function () {
      return linearScale([ -10, 42 ], [ this.get( 'height' ) - 20, 25 ]);
    }
  }
});

// this returns a function that scales a value from a
// given domain to a given range. Hat-tip to D3
function linearScale ( domain, range ) {
  var d0 = domain[0], r0 = range[0], multiplier = ( range[1] - r0 ) / ( domain[1] - d0 );

  return function ( num ) {
    return r0 + ( ( num - d0 ) * multiplier );
  };
}

// Because we're using SVG, we need to recalculate
// scales when the container resizes
function resize () {
  var wrapper = ractive.find( '.svg-wrapper' );

  ractive.set({
    width: wrapper.clientWidth,
    height: wrapper.clientHeight
  });
};

// For the purposes of this tutorial, we've got a global
// onResize function which lets us safely add resize handlers
onResize( resize );
setTimeout( resize );

setup:
(function () {
	var promises = {};

	window.getJSON = function ( url ) {
		if ( !promises[ url ] ) {
			promises[ url ] = new Ractive.Promise( function ( fulfil ) {
				var xhr = new XMLHttpRequest();

				xhr.onload = function () {
					fulfil( JSON.parse( xhr.responseText ) );
				};

				xhr.open( 'GET', url );
				xhr.send();
			});
		}

		return promises[ url ];
	};
}());

window.format = function ( val ) {
	// Pro-tip: we're using `this.get()` inside this function -
	// as a result, Ractive knows that this computation depends
	// on the value of `degreeType` as well as `val`
	if ( this.get( 'degreeType' ) === 'fahrenheit' ) {
		// convert celsius to fahrenheit
		val = ( val * 1.8 ) + 32;
	}

	return val.toFixed( 1 ) + '°';
};

styles:
#output-block h2 {
	margin: 0 0 0.5em 0;
}

svg {
	width: 100%;
	height: 100%;
}

.svg-wrapper {
	width: 100%;
	height: 100%;
}

.temperatures {
	position: relative;
	width: 100%;
	height: 100%;
	padding: 4em 0 0 0;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

.header {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 2em;
}

.radio-group {
	display: inline-block;
	float: right;
	text-align: right;
	padding: 0.5em 0 0 0;
}

.header h2 {
	float: left;
	margin: 0;
}

.header select {
	position: relative;
	top: 0.1em;
	float: left;
	clear: left;
	font-size: inherit;
	font-family: inherit;
	z-index: 7;
}

.header label {
	position: relative;
	z-index: 7;
}

.header p {
	float: left;
	clear: left;
	margin: 0;
}

.bar-chart {
	position: relative;
	padding: 0 0 3em 0;
	width: 100%;
	height: 100%;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

.temperature-band {
	fill-opacity: 0.3;
	stroke-width: 2;
}

.freezing {
	stroke: #ccc;
	stroke-width: 1;
}

.marker circle {
	fill: white;
	stroke: black;
	stroke-width: 1;
}

.marker	text {
	text-anchor: middle;
	font-family: 'Helvetica Neue', 'Arial';
	font-size: 0.6em;
	font-weight: bold;
	fill: #333;
}

.month-labels {
	position: absolute;
	left: 0;
	bottom: 0;
	width: 100%;
}

.month-labels span {
	text-align: center;
	float: left;
	display: block;
	font-family: 'Helvetica Neue', 'Arial';
	font-size: 0.6em;
}
