javascript:
var ractive = new Ractive({
  el: output,
  template: template,
  data: { visible: 1 }
});

ractive.on({
  show: function ( event, which ) {
    this.set( 'visible', which );
  }
});

template:
{{#if visible === 1 }}
  <button intro='fade' on-tap='show:2'>Click me!</button>
{{elseif visible === 2 }}
  <button intro='slide' on-tap='show:3'>And me!</button>
{{elseif visible === 3 }}
  <button intro='fly' on-tap='show:1'>Now click me!</button>
{{/if}}

fixed.javascript:
var ractive = new Ractive({
  el: output,
  template: template,
  data: { visible: 1 }
});

ractive.on({
  show: function ( event, which ) {
    ractive.set( 'visible', null ).then( function () {
      ractive.set( 'visible', which );
    });
  }
});

fixed.template:
{{#if visible === 1 }}
  <button intro='fade' outro='fly' on-tap='show:2'>Click me!</button>
{{elseif visible === 2 }}
  <button intro='slide' outro='fade' on-tap='show:3'>And me!</button>
{{elseif visible === 3 }}
  <button intro='fly' outro='slide' on-tap='show:1'>Now click me!</button>
{{/if}}

styles:
#output-block button {
	display: block;
	position: relative;
	padding: 1em;
	font-size: 1.6em;
	text-align: center;
	margin: 0 auto;
}
